import React from 'react';
import {
    View,
    Text,
    SafeAreaView,
    TouchableOpacity,
    StyleSheet
} from 'react-native';

import { Ionicons } from '@expo/vector-icons';
import { MaterialIcons } from '@expo/vector-icons';

const CourseHome = () => {
    function renderHeader(){
        return(
            <View>
                <View>
                    <Ionicons name="arrow-back" size={24} color="black" />
                    <MaterialIcons name="message" size={24} color="black" />
                </View>
                <View>
                    <Text>GHS Atiela</Text>
                    <Text>Biology Fundamentals</Text>
                    <Text>120 learners</Text>
                </View>
                <View>
                    <TouchableOpacity>
                        <Text>lessons</Text>
                    </TouchableOpacity>
                    <TouchableOpacity>
                        <Text>quizzes</Text>
                    </TouchableOpacity>
                    <TouchableOpacity>
                        <Text>references</Text>
                    </TouchableOpacity>
                </View>
            </View>
        )
    }
    return(
        <SafeAreaView style={styles.container}>
            {renderHeader()}
        </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    container:{
        flex: 1,
        backgroundColor: COLORS.lightGray4,
        top: 30
    },
    shadow: {
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 3,
        },
        shadowOpacity: 0.1,
        shadowRadius: 3,
        elevation: 1,
    }
})


export default CourseHome;
