import React from 'react';
import {
  View, 
  StyleSheet, 
  TouchableOpacity, 
  Image} from 'react-native';
import { 
  Stack, 
  TextInput, 
  IconButton,
  Text,
  Button } from "@react-native-material/core";
import Icon from "@expo/vector-icons/MaterialCommunityIcons";

const logo = require('../../assets/logo.png');

const ScreenOne = props => {

    //onPress To Navigate
    const onPress = () => {
      props.navigation.navigate('Register - Role');
    };
  
    return (
      <View style={styles.main}>
        <Image
          style={styles.stretch}
          source={require('../../assets/logo.png')}
        />
        <Text variant="h6">Welcome to e-learn</Text>
        <Stack spacing={2} style={{ margin: 16, width: "90%", padding: 8 }}>
          <TextInput
            label="username or email"
            leading={props => <Icon name="account" {...props} />}
            variant="outlined"
            style={{borderColor: "#2196F3"}}
          />
          <TextInput
            label="password"
            variant="outlined"
            style={{borderColor: "#2196F3"}}
            trailing={props => (
              <IconButton icon={props => <Icon name="eye" {...props} />} {...props} />
            )}
          />
          <TextInput
            label="username"
            leading={props => <Icon name="account" {...props} />}
            variant="outlined"
            style={{borderColor: "#2196F3"}}
          />
          <TextInput
            label="Which institution are you part of?"
            variant="outlined"
            style={{borderColor: "#2196F3"}}
            leading={props => <Icon name="school" {...props} />}
          />
          <Button title="Sign In" color="#2196F3" />
        </Stack>
        <TouchableOpacity onPress={onPress}>
          <Text color='#2196F3'>Click here to sign up</Text>
        </TouchableOpacity>
      </View>
    );
  };

const styles = StyleSheet.create({
  main:{
    backgroundColor: "#FFFFFF",
    flex: 1, 
    justifyContent: 'center', 
    alignItems: 'center'
  },
  stretch: {
    width: 50,
    height: 50,
    resizeMode: 'stretch',
  },
})
  
export default ScreenOne;
