import React from 'react';
import {
    View,
    Text,
    SafeAreaView,
    TouchableOpacity,
    StyleSheet
} from 'react-native';

import { Ionicons } from '@expo/vector-icons';
import { MaterialIcons } from '@expo/vector-icons';

import { COLORS, SIZES } from '../../constants';

const CourseHome = () => {
    function renderHeader(){
        return(
            <View>
                <View
                    style={{
                        flexDirection: "row",
                        justifyContent: "space-between",
                        padding: SIZES.padding
                    }}
                >
                    <Ionicons name="arrow-back" size={30} color="black" />
                    <MaterialIcons name="message" size={30} color="black" />
                </View>
                <View
                 style={{
                    flexDirection: "column",
                    alignItems: "center",
                    justifyContent: "center"
                 }}
                >
                    <Text
                        style={{
                            color: COLORS.primary,
                            fontSize: 16
                        }}
                    >GHS Atiela</Text>
                    <Text
                        style={{
                            fontSize: 24,
                            fontWeight: "bold"
                        }}
                    >Biology Fundamentals</Text>
                    <Text
                        style={{
                            fontSize: 14
                        }}
                    >120 learners</Text>
                </View>
                <View
                    style={{
                        flexDirection: "row",
                        alignItems: "center",
                        justifyContent: "space-around"
                    }}
                >
                    <TouchableOpacity>
                        <Text>lessons</Text>
                    </TouchableOpacity>
                    <TouchableOpacity>
                        <Text>quizzes</Text>
                    </TouchableOpacity>
                    <TouchableOpacity>
                        <Text>references</Text>
                    </TouchableOpacity>
                </View>
            </View>
        )
    }
    return(
        <SafeAreaView style={styles.container}>
            {renderHeader()}
        </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    container:{
        flex: 1,
        backgroundColor: COLORS.lightGray4,
        top: 30
    },
    shadow: {
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 3,
        },
        shadowOpacity: 0.1,
        shadowRadius: 3,
        elevation: 1,
    }
})


export default CourseHome;
