import React from 'react';
import {
    View,
    Text,
    SafeAreaView,
    TouchableOpacity,
    StyleSheet,
    ImageBackground
} from 'react-native';

import { Ionicons } from '@expo/vector-icons';
import { MaterialIcons } from '@expo/vector-icons';
import { Avatar, Card } from 'react-native-paper';
import { Button } from '@react-native-material/core';

const LeftContent = props => <Avatar.Icon {...props} icon="folder" />

import { COLORS, SIZES, images } from '../../constants';

const CourseHome = () => {
    function renderHeader(){
        return(
            <View>
                <ImageBackground 
                    source={{uri: "https://i.postimg.cc/Vk3M7SSy/acadhomelink.jpg"}}
                    resizeMode="cover"
                    style={{
                        justifyContent: "center",
                        opacity: 0.7
                    }}
                >
                    <View
                        style={{
                            flexDirection: "row",
                            justifyContent: "space-between",
                            padding: SIZES.padding
                        }}
                    >
                        <Ionicons name="arrow-back" size={30} color="black" />
                        <MaterialIcons name="message" size={30} color="black" />
                    </View>
                    <View
                    style={{
                        flexDirection: "column",
                        alignItems: "center",
                        justifyContent: "center",
                    }}
                    >
                        <Text
                            style={{
                                color: COLORS.primary,
                                fontSize: 16,
                                fontWeight: "bold"
                            }}
                        >GHS Atiela</Text>
                        <Text
                            style={{
                                fontSize: 24,
                                fontWeight: "bold",
                            }}
                        >Biology Fundamentals</Text>
                        <Text
                            style={{
                                fontSize: 14,
                                color: COLORS.primary,
                                fontWeight: "bold"
                            }}
                        >120 learners</Text>
                    </View>
                    <View
                        style={{
                            flexDirection: "row",
                            alignItems: "center",
                            justifyContent: "center",
                            marginTop: SIZES.padding*2+6
                        }}
                    >
                        <TouchableOpacity
                            style={{
                                // marginRight: SIZES.padding*2,
                                borderColor: COLORS.blue,
                                padding: SIZES.padding,
                                borderWidth: 3,
                                borderBottomWidth: 0,
                                borderRadius: SIZES.padding
                            }}
                        >
                            <Text style={{fontWeight:"bold"}}>lessons</Text>
                        </TouchableOpacity>
                        <TouchableOpacity
                            style={{
                                // marginRight: SIZES.padding*2,
                                borderColor: COLORS.blue,
                                padding: SIZES.padding,
                                borderBottomWidth: 3,
                                // borderBottomWidth: 0,
                                borderRadius: SIZES.padding,
                            }}
                        >
                            <Text 
                            style={{fontWeight:"bold", color:COLORS.blue}}>
                                quizzes</Text>
                        </TouchableOpacity>
                        <TouchableOpacity
                            style={{
                                marginRight: SIZES.padding*2,
                                borderColor: COLORS.blue,
                                padding: SIZES.padding,
                                // borderWidth: 3,
                                borderBottomWidth: 3,
                                borderRadius: SIZES.padding
                            }}
                        >
                            <Text 
                                style={{fontWeight:"bold", color:COLORS.blue}}
                                >references</Text>
                        </TouchableOpacity>
                    </View>
                </ImageBackground>
            </View>
        )
    }

    function renderStartLearning(){
        return(
            <Card>
                <Card.Content>
                    <Text variant="titleLarge">
                    Yorem ipsum dolor sit amet, consectetur adipiscing elit. 
                    Nunc vulputate libero et velit interdum, ac aliquet odio mattis. Class aptent 
                    </Text>
                </Card.Content>
                <Card.Content>
                    <Button
                        title="Start Learning"
                        style={{
                            borderRadius: 0,
                            width: "70%"
                        }}
                    />
                </Card.Content>
            </Card>
        )
    }

    return(
        <SafeAreaView style={styles.container}>
            {renderHeader()}
            {renderStartLearning()}
        </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    container:{
        flex: 1,
        backgroundColor: COLORS.lightGray4,
        top: 30
    },
    shadow: {
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 3,
        },
        shadowOpacity: 0.1,
        shadowRadius: 3,
        elevation: 1,
    }
})


export default CourseHome;
